html,
body {
  margin: 0;
  padding: 0;
  font-family: sans-serif;
  background-color: white;
  overflow-x: hidden;
  font-family: "Gilory", sans-serif;
}

.header {
  display: flex;
  width: 100vw;
  justify-content: space-between;
  padding: 3vw;
  position: fixed;
  z-index: 2;
}
.header .header__logo {
  width: 30px;
  width: 30px;
}
.header .header__nav a {
  padding-left: 20px;
  font-size: 16px;
  font-weight: bold;
}

.teaser__steps {
  left: 3vw;
  top: 30vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: fixed;
}
.teaser__steps .steps__scala {
  height: 100px;
  border-radius: 2vw;
  width: 2px;
  background-color: #efefef;
}
.teaser__steps .stes__number {
  margin: 10px;
  font-weight: bold;
}
.teaser__steps .stes__number--position {
  transition: all 0.5s;
  font-size: auto;
}
.teaser__steps .stes__number--position.active {
  animation: scaleNumber 0.5s alternate;
}
@keyframes scaleNumber {
  0% {
    font-size: 16px;
  }
  100% {
    font-size: 20px;
  }
}

.teaser {
  margin-right: 3vw;
  margin-left: 20vw;
  width: 77vw;
  max-width: 1100px;
  margin-bottom: 30px;
}
@media (min-width: 750px) {
  .teaser {
    display: flex;
    justify-content: space-between;
    margin-bottom: 70px;
  }
}
@media (min-width: 750px) {
  .teaser .teaser__textbox {
    margin-top: 90px;
  }
}
@media (min-width: 750px) {
  .teaser .teaser__textbox {
    width: 28%;
  }
}
.teaser .teaser__textbox .teaser__headline,
.teaser .teaser__textbox .teaser__subheadline {
  text-align: right;
  width: 100%;
  display: block;
}
.teaser .teaser__textbox .teaser__headline {
  font-size: 35px;
  font-weight: bold;
  margin-top: 70px;
}
.teaser .teaser__textbox .teaser__subheadline {
