  margin-top: 10px;
}
@media (max-width: 749px) {
  .teaser .teaser__textbox .teaser__subheadline {
    margin-bottom: 30px;
  }
}
.teaser .teaser__textbox .teaser__text {
  margin-top: 70px;
  width: 80%;
}
@media (max-width: 749px) {
  .teaser .teaser__textbox .teaser__text {
    margin-top: 145px;
    width: 80%;
