.textSlide {
  position: relative;
}
@media (min-width: 750px) {
  .textSlide .textSlideBox {
    position: relative;
    left: 230px;
    top: -40px;
  }
}

.textSlideBox {
  display: inline-block;
  height: 1.7em;
  overflow: hidden;
}
.textSlideBox .textSlideBox__words {
  animation-name: wordSlider;
  animation-timing-function: ease-out;
  animation-iteration-count: infinite;
  animation-duration: 7s;
}
.textSlideBox .textSlideBox__word {
  display: block;
  line-height: 30px;
  text-align: left;
  font-size: 30px;
}

@keyframes wordSlider {
  0%, 27% {
    transform: translateY(0%);
  }
  33%, 60% {
    transform: translateY(-25%);
  }
  66%, 93% {
    transform: translateY(-50%);
  }
  100% {
    transform: translateY(-75%);
  }
}
.page--bw__intro__headline {
